import { Link, useNavigate } from 'react-router-dom';
import React, { useState } from 'react';
import axios from 'axios';
import './Navbar.css'; // Import the provided Navbar.css file

function Home() {
  const [notificationMessage, setNotificationMessage] = useState('');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const navigate = useNavigate();

  // Function to send notification
  const sendNotification = async () => {
    try {
      await axios.post('http://localhost:8000/api/notifications', { message: notificationMessage });
      console.log('Notification sent');
      setNotificationMessage('');
    } catch (error) {
      console.error('Error sending notification:', error);
    }
  };

  // Function to navigate to EditAdmin page
  const goToEditAdmin = () => {
    navigate('/edit-admin');
    setIsMenuOpen(false); // Close the menu after navigation
  };

  // Function to navigate to Approve page
  const goToApproveResidents = () => {
    navigate('/approve-residents');
    setIsMenuOpen(false); // Close the menu after navigation
  };

  // Function to toggle menu
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div className="header">
      <div className="nav">
        <div className="nav__logo">
          <img src="/path/to/profile-photo.jpg" alt="Profile" style={{ width: '40px', height: '40px', borderRadius: '50%', marginRight: '10px' }} />
          <span style={{ fontWeight: 'bold' }}>Roohullah</span>
        </div>
        <button onClick={toggleMenu} className="nav__toggle">
          â˜° Menu
        </button>
      </div>

      {/* Menu */}
      <div className={isMenuOpen ? 'nav__list active' : 'nav__list'}>
        <button onClick={goToEditAdmin} className="nav__cta">Edit Profile</button>
        <button onClick={goToApproveResidents} className="nav__cta">Approve Residents</button>
        <button onClick={() => setIsMenuOpen(false)} className="nav__cta">Logout</button>
      </div>
    </div>
  );
}

export default Home;
