import { Link } from 'react-router-dom';
import React, { useState } from 'react';
import axios from 'axios';

function Home() {
  const [notificationMessage, setNotificationMessage] = useState('');
  const [adminData, setAdminData] = useState({
    name: 'xyz',
    picture: '',
    contact: 'Admin Contact',
    address: 'Admin Address',
  });
  const [editMode, setEditMode] = useState(false); // State to toggle edit mode
  const [editedAdminData, setEditedAdminData] = useState({}); // State to hold edited admin data

  // Function to send notification
  const sendNotification = async () => {
    try {
      await axios.post('http://localhost:8000/api/notifications', { message: notificationMessage });
      console.log('Notification sent');
      setNotificationMessage('');
    } catch (error) {
      console.error('Error sending notification:', error);
    }
  };

  // Function to handle changes in admin data
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedAdminData({
      ...editedAdminData,
      [name]: value,
    });
  };

  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    setAdminData(editedAdminData);
    setEditMode(false);
  };

  // Function to handle image upload
  const handleImageUpload = (event) => {
    setEditedAdminData({
      ...editedAdminData,
      picture: URL.createObjectURL(event.target.files[0]),
    });
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', minHeight: '100vh', backgroundColor: '#2980b9', color: '#000', padding: '20px' }}>
      <h1 style={{ marginBottom: '20px', color: '#fff' }}>Society Connect <br></br> Admin Dashboard</h1>

      {/* Edit Profile Button */}
      <button onClick={() => setEditMode(true)} style={{ position: 'absolute', top: '10px', right: '10px', background: 'none', border: 'none', cursor: 'pointer', color: '#fff' }}>
        <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-edit-2">
          <path d="M16.5 3a2.121 2.121 0 0 1 3 3L6 21l-4 1 1-4 12-12a2.121 2.121 0 0 1 3 3z"></path>
        </svg>
      </button>

      {/* Admin Profile */}
      <div style={{ backgroundColor: '#fff', color: '#000', padding: '20px', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', marginBottom: '20px', width: '80%', position: 'relative' }}>
        <h2>Admin Profile</h2>
        <div>
          <img src={adminData.picture} alt="Admin" style={{ width: '150px', height: '150px', borderRadius: '50%', marginBottom: '10px' }} />
          <div><strong>Name:</strong> {adminData.name}</div>
          <div><strong>Contact:</strong> {adminData.contact}</div>
          <div><strong>Address:</strong> {adminData.address}</div>
        </div>
      </div>

      {/* Edit Profile Form */}
      {editMode && (
        <div style={{ backgroundColor: '#fff', color: '#000', padding: '20px', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', marginBottom: '20px', width: '80%' }}>
          <h2>Edit Profile</h2>
          <form onSubmit={handleSubmit}>
            <label>Name:</label>
            <input type="text" name="name" value={editedAdminData.name || adminData.name} onChange={handleInputChange} style={{ marginBottom: '10px', width: '100%' }} />
            <label>Contact:</label>
            <input type="text" name="contact" value={editedAdminData.contact || adminData.contact} onChange={handleInputChange} style={{ marginBottom: '10px', width: '100%' }} />
            <label>Address:</label>
            <input type="text" name="address" value={editedAdminData.address || adminData.address} onChange={handleInputChange} style={{ marginBottom: '10px', width: '100%' }} />
            <label>Profile Picture:</label>
            <input type="file" accept="image/*" onChange={handleImageUpload} style={{ marginBottom: '10px' }} />
            <button type="submit" style={{ padding: '8px 16px', cursor: 'pointer' }}>Save</button>
          </form>
        </div>
      )}

      {/* Generate Notification */}
      <div style={{ backgroundColor: '#fff', color: '#000', padding: '20px', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', marginBottom: '20px', width: '80%' }}>
        <h2>Generate Notification</h2>
        <textarea
          value={notificationMessage}
          onChange={(e) => setNotificationMessage(e.target.value)}
          placeholder="Enter notification message..."
          style={{ width: '100%', minHeight: '100px', marginBottom: '10px', padding: '5px' }}
        />
        <button onClick={sendNotification} style={{ padding: '8px 16px', cursor: 'pointer' }}>Send Notification</button>
      </div>

      <br />
      <Link to="/" style={{ color: '#fff' }}>Logout</Link>
    </div>
  );
}

export default Home;
