import { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import SocietyConnectLogo from '../components/logo.jpeg'; // Import your Society Connect logo image

function Home() {
  const [notificationMessage, setNotificationMessage] = useState('');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [userData, setUserData] = useState({
    name: 'Roohullah',
    profilePicture: '/path/to/profile-photo.jpg'
  });
  const navigate = useNavigate();

  useEffect(() => {
    const storedUserData = window.localStorage.getItem('userData');
    if (storedUserData) {
      setUserData(JSON.parse(storedUserData));
    }
  }, []);

  // Function to send notification
  const sendNotification = async () => {
    try {
      await axios.post('http://localhost:8000/api/notifications', { message: notificationMessage });
      console.log('Notification sent');
      setNotificationMessage('');
    } catch (error) {
      console.error('Error sending notification:', error);
    }
  };

  // Function to navigate to EditAdmin page
  const goToEditAdmin = () => {
    navigate('/EditAdmin');
    setIsMenuOpen(false); // Close the menu after navigation
  };

  // Function to navigate to Approve page
  const goToApproveResidents = () => {
    navigate('/approve-residents');
    setIsMenuOpen(false); // Close the menu after navigation
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh', backgroundColor: '#f0f0f0', color: '#000' }}>
      {/* Header */}
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '20px', borderBottom: '1px solid #ccc' }}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <img src={SocietyConnectLogo} alt="Society Connect Logo" style={{ width: '60px', height: '60px', borderRadius: '50%', border: '2px solid #28a745', marginRight: '10px' }} />
          <h1 style={{ margin: 0, fontWeight: 'bold', fontSize: '24px' }}>Society Connect Administration</h1>
          {/* Menu */}
          {isMenuOpen && (
            <div style={{ marginLeft: '20px', display: 'flex', flexDirection: 'column', alignItems: 'flex-start', backgroundColor: '#fff', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', padding: '20px', zIndex: '9998', width: '200px', textAlign: 'left' }}>
              <button onClick={goToEditAdmin} style={{ marginBottom: '10px', padding: '10px 20px', cursor: 'pointer', backgroundColor: '#2980b9', color: '#fff', border: 'none', borderRadius: '5px', width: '100%', display: 'block', textAlign: 'center', transition: 'background-color 0.3s' }}>Edit Profile</button>
              <button onClick={goToApproveResidents} style={{ marginBottom: '10px', padding: '10px 20px', cursor: 'pointer', backgroundColor: '#2980b9', color: '#fff', border: 'none', borderRadius: '5px', width: '100%', display: 'block', textAlign: 'center', transition: 'background-color 0.3s' }}>Approve Residents</button>
              <button onClick={() => setIsMenuOpen(false)} style={{ padding: '10px 20px', cursor: 'pointer', backgroundColor: '#ccc', color: '#333', border: 'none', borderRadius: '5px', width: '100%', display: 'block', textAlign: 'center', transition: 'background-color 0.3s' }}>Logout</button>
            </div>
          )}
        </div>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <img src={userData.profilePicture} alt="Profile" style={{ width: '60px', height: '60px', borderRadius: '50%', border: '2px solid #28a745', marginRight: '10px' }} />
          <span style={{ marginRight: '10px' }}>{userData.name}</span>
        </div>
      </header>

      {/* Body */}
      <main style={{ flex: 1, padding: '20px', display: 'flex', justifyContent: 'center', alignItems: 'center', textAlign: 'center' }}>
        <div style={{ backgroundColor: '#fff', color: '#000', padding: '20px', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', width: '80%' }}>
          <h2 style={{ color: '#2980b9', marginBottom: '20px' }}>Generate Notification</h2>
          <textarea
            value={notificationMessage}
            onChange={(e) => setNotificationMessage(e.target.value)}
            placeholder="Enter notification message..."
            style={{ width: '100%', minHeight: '200px', marginBottom: '20px', padding: '10px', borderRadius: '5px', border: '1px solid #ccc', resize: 'vertical' }}
          />
          <button onClick={sendNotification} style={{ padding: '12px 20px', borderRadius: '5px', border: 'none', background: '#2980b9', color: '#fff', fontSize: '16px', fontWeight: 'bold', cursor: 'pointer', boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)', transition: 'background-color 0.3s' }}>Send Notification</button>
        </div>
      </main>

      {/* Footer */}
      <footer style={{ display: 'flex', justifyContent: 'center', padding: '20px', borderTop: '1px solid #ccc' }}>
        <Link to="/" style={{ padding: '10px 20px', textDecoration: 'none', color: '#2980b9', borderRadius: '5px', border: '1px solid #2980b9' }}>Logout</Link>
      </footer>
    </div>
  );
}

export default Home;
