import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Drawer from '@mui/material/Drawer';// Import Drawer component from Material-UI

function Home() {
  const [notificationMessage, setNotificationMessage] = useState('');
  const [notificationTime, setNotificationTime] = useState('');
  const [isDrawerOpen, setIsDrawerOpen] = useState(false); // State for drawer
  const [userData, setUserData] = useState({
    name: 'Roohullah',
    profilePicture: '/path/to/profile-photo.jpg'
  });
  const navigate = useNavigate();
  const [isProfileEnlarged, setIsProfileEnlarged] = useState(false);

  useEffect(() => {
    const storedUserData = window.localStorage.getItem('userData');
    if (storedUserData) {
      setUserData(JSON.parse(storedUserData));
    }
  }, []);

  // Function to send notification
  const sendNotification = async () => {
    try {
      await axios.post('http://localhost:8000/api/notifications', { message: notificationMessage });
      console.log('Notification sent');
      setNotificationMessage('');
    } catch (error) {
      console.error('Error sending notification:', error);
    }
  };

  // Function to send scheduled notification
  const sendScheduledNotification = () => {
    // Validate notification time
    if (!notificationTime) {
      alert('Please set a notification time.');
      return;
    }

    // Convert notification time to milliseconds
    const scheduledTime = new Date(notificationTime).getTime();
    const currentTime = new Date().getTime();

    // Validate if the scheduled time is in the future
    if (scheduledTime <= currentTime) {
      alert('Please set a future notification time.');
      return;
    }

    // Calculate delay until notification time
    const delay = scheduledTime - currentTime;

    // Display notification schedule message
    const scheduledTimeString = new Date(scheduledTime).toLocaleString();
    alert(`Notification scheduled for ${scheduledTimeString}.`);

    // Set timeout to send notification
    setTimeout(() => {
      sendNotification();
    }, delay);
  };

  // Function to navigate to EditAdmin page
  const goToEditAdmin = () => {
    navigate('/EditAdmin');
    setIsDrawerOpen(false); // Close the drawer after navigation
  };

  // Function to navigate to Approve page
  const goToApproveResidents = () => {
    navigate('/Approve');
    setIsDrawerOpen(false); // Close the drawer after navigation
  };

  // Function to toggle profile picture size
  const toggleProfileSize = () => {
    setIsProfileEnlarged(!isProfileEnlarged);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'stretch', minHeight: '100vh', backgroundColor: '#f0f0f0', color: '#000' }}>
      {/* Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '20px', borderBottom: '1px solid #ccc' }}>
        <button onClick={() => setIsDrawerOpen(true)} style={{ padding: '10px', cursor: 'pointer', backgroundColor: 'transparent', border: 'none', borderRadius: '5px', fontSize: '16px', color: '#333', fontWeight: 'bold', textDecoration: 'underline' }}>
          ☰ Menu
        </button>
        <div style={{ display: 'flex', alignItems: 'center', fontWeight: 'bold', fontSize: '18px', flex: '1', justifyContent: 'center' }}>
          <span>Admin Dashboard</span>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', position: 'relative' }}>
          <img 
            src={userData.profilePicture} 
            alt="Profile" 
            style={{ 
              width: isProfileEnlarged ? '120px' : '60px', 
              height: isProfileEnlarged ? '120px' : '60px', 
              borderRadius: '50%', 
              border: '2px solid #28a745', 
              marginRight: '10px',
              cursor: 'pointer',
              transition: 'width 0.3s, height 0.3s'
            }} 
            onClick={toggleProfileSize}
          />
          <span style={{ marginRight: '10px' }}>{userData.name}</span>
        </div>
      </div>

      {/* Drawer */}
      <Drawer anchor="left" open={isDrawerOpen} onClose={() => setIsDrawerOpen(false)}>
        <div style={{ width: '250px' }}>
          <button onClick={goToEditAdmin} style={{ marginBottom: '10px', padding: '10px 20px', cursor: 'pointer', backgroundColor: '#2980b9', color: '#fff', border: 'none', borderRadius: '5px', width: '100%', display: 'block', textAlign: 'center', transition: 'background-color 0.3s', fontWeight: 'bold', fontSize: '16px', boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)' }}>Edit Profile</button>
          <button onClick={goToApproveResidents} style={{ marginBottom: '10px', padding: '10px 20px', cursor: 'pointer', backgroundColor: '#2980b9', color: '#fff', border: 'none', borderRadius: '5px', width: '100%', display: 'block', textAlign: 'center', transition: 'background-color 0.3s', fontWeight: 'bold', fontSize: '16px', boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)' }}>Approve Residents</button>
          <button onClick={() => setIsDrawerOpen(false)} style={{ marginBottom: '10px', padding: '10px 20px', cursor: 'pointer', backgroundColor: '#ccc', color: '#333', border: 'none', borderRadius: '5px', width: '100%', display: 'block', textAlign: 'center', transition: 'background-color 0.3s', fontWeight: 'bold', fontSize: '16px', boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)' }}>Old Notifications</button>
          <button onClick={() => setIsDrawerOpen(false)} style={{ padding: '10px 20px', cursor: 'pointer', backgroundColor: '#ccc', color: '#333', border: 'none', borderRadius: '5px', width: '100%', display: 'block', textAlign: 'center', transition: 'background-color 0.3s', fontWeight: 'bold', fontSize: '16px', boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)' }}>Logout</button>
        </div>
      </Drawer>

      {/* Body */}
      {/* Body content remains the same */}

      {/* Footer */}
      {/* Footer content remains the same */}
    </div>
  );
}

export default Home;
