import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

function EditAdmin() {
  const [editedAdminData, setEditedAdminData] = useState({
    name: 'xyz',
    picture: '',
    contact: 'Admin Contact',
    address: 'Admin Address',
  });

  const history = useHistory();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedAdminData({
      ...editedAdminData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Implement save functionality here, for now just navigate back to home
    history.push('/');
  };

  const handleImageUpload = (event) => {
    setEditedAdminData({
      ...editedAdminData,
      picture: URL.createObjectURL(event.target.files[0]),
    });
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', minHeight: '100vh', backgroundColor: '#f0f0f0', color: '#000', padding: '20px' }}>
      <h1 style={{ color: '#2980b9', marginBottom: '20px' }}>Edit Admin Profile</h1>

      <form onSubmit={handleSubmit} style={{ width: '100%', maxWidth: '500px' }}>
        <label style={{ marginBottom: '10px' }}>Name:</label>
        <input type="text" name="name" value={editedAdminData.name} onChange={handleInputChange} style={{ marginBottom: '10px', width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
        <label style={{ marginBottom: '10px' }}>Contact:</label>
        <input type="text" name="contact" value={editedAdminData.contact} onChange={handleInputChange} style={{ marginBottom: '10px', width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
        <label style={{ marginBottom: '10px' }}>Address:</label>
        <input type="text" name="address" value={editedAdminData.address} onChange={handleInputChange} style={{ marginBottom: '10px', width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
        <label style={{ marginBottom: '10px' }}>Profile Picture:</label>
        <input type="file" accept="image/*" onChange={handleImageUpload} style={{ marginBottom: '10px' }} />
        <button type="submit" style={{ padding: '8px 16px', cursor: 'pointer', backgroundColor: '#2980b9', color: '#fff', border: 'none', borderRadius: '5px' }}>Save</button>
      </form>
    </div>
  );
}

export default EditAdmin;
