import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function EditProfile() {
  const [name, setName] = useState('Roohullah');
  const [profilePicture, setProfilePicture] = useState('/path/to/profile-photo.jpg');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleProfilePictureChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setProfilePicture(reader.result);
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const saveChanges = () => {
    // Simulate API call to save changes
    setTimeout(() => {
      setSuccessMessage('Profile successfully updated!');
      setErrorMessage('');
      // Pass updated data to Home component
      window.localStorage.setItem('userData', JSON.stringify({ name, profilePicture }));
      // Navigate back to home
      navigate('/');
    }, 1000);
  };

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <div style={{ marginBottom: '20px', display: 'flex', alignItems: 'center', justifyContent: 'flex-start' }}>
        <Link to="/" style={{ textDecoration: 'none', color: '#333', marginRight: '10px' }}>
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="16"
            height="16"
            fill="currentColor"
            className="bi bi-arrow-left"
            viewBox="0 0 16 16"
          >
            <path
              fillRule="evenodd"
              d="M11.354 11.354a.5.5 0 0 0 0-.708l-7-7a.5.5 0 1 0-.708.708L10.293 10l-6.647 6.646a.5.5 0 0 0 .708.708l7-7z"
            />
          </svg>
        </Link>
        <h2 style={{ marginBottom: '20px', display: 'inline-block' }}>Edit Profile</h2>
      </div>
      <div style={{ marginBottom: '20px' }}>
        <label htmlFor="name">Name:</label>
        <input type="text" id="name" value={name} onChange={handleNameChange} />
      </div>
      <div style={{ marginBottom: '20px' }}>
        <label htmlFor="profile-picture">Profile Picture:</label>
        <input type="file" id="profile-picture" onChange={handleProfilePictureChange} />
      </div>
      <div style={{ marginBottom: '20px' }}>
        <img src={profilePicture} alt="Profile" style={{ width: '150px', height: '150px', borderRadius: '50%' }} />
      </div>
      <button onClick={saveChanges} style={{ padding: '10px 20px', cursor: 'pointer', backgroundColor: '#2980b9', color: '#fff', border: 'none', borderRadius: '5px' }}>Save Changes</button>
      {successMessage && <p style={{ color: 'green', marginTop: '10px' }}>{successMessage}</p>}
      {errorMessage && <p style={{ color: 'red', marginTop: '10px' }}>{errorMessage}</p>}
    </div>
  );
}

export default EditProfile;
