import { Link, useNavigate } from 'react-router-dom';
import React, { useState } from 'react';
import axios from 'axios';

function Home() {
  const [notificationMessage, setNotificationMessage] = useState('');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const navigate = useNavigate();

  // Function to send notification
  const sendNotification = async () => {
    try {
      await axios.post('http://localhost:8000/api/notifications', { message: notificationMessage });
      console.log('Notification sent');
      setNotificationMessage('');
    } catch (error) {
      console.error('Error sending notification:', error);
    }
  };

  // Function to navigate to EditAdmin page
  const goToEditAdmin = () => {
    navigate('/edit-admin');
  };

  // Function to navigate to Approve page
  const goToApproveResidents = () => {
    navigate('/approve-residents');
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', minHeight: '100vh', backgroundColor: '#f0f0f0', color: '#000' }}>
      {/* Generate Notification */}
      <div style={{ backgroundColor: '#fff', color: '#000', padding: '20px', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', marginBottom: '20px', width: '50%' }}>
        <h2 style={{ color: '#2980b9', marginBottom: '20px' }}>Generate Notification</h2>
        <textarea
          value={notificationMessage}
          onChange={(e) => setNotificationMessage(e.target.value)}
          placeholder="Enter notification message..."
          style={{ width: '100%', minHeight: '100px', marginBottom: '10px', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }}
        />
        <button onClick={sendNotification} style={{ padding: '8px 16px', cursor: 'pointer', backgroundColor: '#2980b9', color: '#fff', border: 'none', borderRadius: '5px', marginRight: '10px' }}>Send Notification</button>
        <Link to="/" style={{ padding: '8px 16px', textDecoration: 'none', color: '#2980b9', borderRadius: '5px', border: '1px solid #2980b9' }}>Logout</Link>
      </div>

      {/* Application Menu */}
      <div style={{ position: 'fixed', top: '20px', left: '20px', backgroundColor: isMenuOpen ? '#add8e6' : 'transparent', borderRadius: '10px', padding: '10px', zIndex: '999', width: '50%' }}>
        <button onClick={() => setIsMenuOpen(!isMenuOpen)} style={{ padding: '10px', cursor: 'pointer', backgroundColor: 'transparent', border: 'none' }}>
          â˜° Menu
        </button>
        {isMenuOpen && (
          <div style={{ backgroundColor: '#fff', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', padding: '10px', marginTop: '10px', width: '100%' }}>
            <button onClick={goToEditAdmin} style={{ marginBottom: '10px', padding: '8px 16px', cursor: 'pointer', backgroundColor: '#2980b9', color: '#fff', border: 'none', borderRadius: '5px', width: '100%', textAlign: 'left' }}>Edit Profile</button>
            <button onClick={goToApproveResidents} style={{ padding: '8px 16px', cursor: 'pointer', backgroundColor: '#2980b9', color: '#fff', border: 'none', borderRadius: '5px', width: '100%', textAlign: 'left' }}>Approve Residents</button>
          </div>
        )}
      </div>
    </div>
  );
}

export default Home;
