import React, { useState } from 'react';
import axios from 'axios';

function EditProfile({ adminData, setAdminData, onClose }) {
  const [editedAdminData, setEditedAdminData] = useState(adminData);

  // Function to handle changes in admin data
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedAdminData({
      ...editedAdminData,
      [name]: value,
    });
  };

  // Function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Call API to save edited admin data
      // For example:
      // await axios.put('http://localhost:8000/api/admin', editedAdminData);
      setAdminData(editedAdminData);
      onClose();
    } catch (error) {
      console.error('Error saving profile:', error);
    }
  };

  // Function to handle image upload
  const handleImageUpload = (event) => {
    setEditedAdminData({
      ...editedAdminData,
      picture: URL.createObjectURL(event.target.files[0]),
    });
  };

  return (
    <div style={{ backgroundColor: '#fff', color: '#000', padding: '20px', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', marginBottom: '20px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <h2 style={{ color: '#2980b9', marginBottom: '20px' }}>Edit Profile</h2>
      <form onSubmit={handleSubmit} style={{ width: '100%' }}>
        <label style={{ marginBottom: '10px' }}>Name:</label>
        <input type="text" name="name" value={editedAdminData.name} onChange={handleInputChange} style={{ marginBottom: '10px', width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
        <label style={{ marginBottom: '10px' }}>Contact:</label>
        <input type="text" name="contact" value={editedAdminData.contact} onChange={handleInputChange} style={{ marginBottom: '10px', width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
        <label style={{ marginBottom: '10px' }}>Address:</label>
        <input type="text" name="address" value={editedAdminData.address} onChange={handleInputChange} style={{ marginBottom: '10px', width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
        <label style={{ marginBottom: '10px' }}>Profile Picture:</label>
        <input type="file" accept="image/*" onChange={handleImageUpload} style={{ marginBottom: '10px' }} />
        <button type="submit" style={{ padding: '8px 16px', cursor: 'pointer', backgroundColor: '#2980b9', color: '#fff', border: 'none', borderRadius: '5px' }}>Save</button>
        <button type="button" onClick={onClose} style={{ marginLeft: '10px', padding: '8px 16px', cursor: 'pointer', backgroundColor: '#ccc', color: '#000', border: 'none', borderRadius: '5px' }}>Cancel</button>
      </form>
    </div>
  );
}

export default EditProfile;
