import React, { useState } from 'react';

function EditProfile() {
  const [name, setName] = useState('Roohullah');
  const [profilePicture, setProfilePicture] = useState('/path/to/profile-photo.jpg');

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleProfilePictureChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setProfilePicture(reader.result);
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const saveChanges = () => {
    // Add logic to save changes, such as sending to backend
    console.log('Name:', name);
    console.log('Profile Picture:', profilePicture);
    // Example API call:
    // axios.post('/api/update-profile', { name, profilePicture });
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Edit Profile</h2>
      <div style={{ marginBottom: '20px' }}>
        <label htmlFor="name">Name:</label>
        <input type="text" id="name" value={name} onChange={handleNameChange} />
      </div>
      <div style={{ marginBottom: '20px' }}>
        <label htmlFor="profile-picture">Profile Picture:</label>
        <input type="file" id="profile-picture" onChange={handleProfilePictureChange} />
      </div>
      <div style={{ marginBottom: '20px' }}>
        <img src={profilePicture} alt="Profile" style={{ width: '100px', height: '100px', borderRadius: '50%' }} />
      </div>
      <button onClick={saveChanges}>Save Changes</button>
    </div>
  );
}

export default EditProfile;
