import { Link, useNavigate } from 'react-router-dom';
import React, { useState } from 'react';
import axios from 'axios';

function Home() {
  const [notificationMessage, setNotificationMessage] = useState('');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const navigate = useNavigate();

  // Function to send notification
  const sendNotification = async () => {
    try {
      await axios.post('http://localhost:8000/api/notifications', { message: notificationMessage });
      console.log('Notification sent');
      setNotificationMessage('');
    } catch (error) {
      console.error('Error sending notification:', error);
    }
  };

  // Function to navigate to EditAdmin page
  const goToEditAdmin = () => {
    navigate('/edit-admin');
    setIsMenuOpen(false); // Close the menu after navigation
  };

  // Function to navigate to Approve page
  const goToApproveResidents = () => {
    navigate('/approve-residents');
    setIsMenuOpen(false); // Close the menu after navigation
  };

  // Function to toggle menu
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div style={{ position: 'fixed', width: '100%', top: 0, left: 0, backgroundColor: 'transparent', zIndex: 'var(--z-fixed)' }}>
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', position: 'relative', height: 'var(--header-height)', margin: '1rem' }}>
        <div style={{ color: 'var(--first-color)', transition: 'color 0.4s', fontSize: 'var(--h2-font-size)', fontFamily: 'var(--second-font)' }}>
          <img src="/path/to/profile-photo.jpg" alt="Profile" style={{ width: '40px', height: '40px', borderRadius: '50%', marginRight: '10px' }} />
          <span style={{ fontWeight: 'bold' }}>Roohullah</span>
        </div>
        <button onClick={toggleMenu} style={{ fontSize: '1.5rem', color: 'var(--title-color)', cursor: 'pointer' }}>â˜° Menu</button>
      </div>

      {/* Menu */}
      <div className={isMenuOpen ? 'nav__list active' : 'nav__list'} style={{ display: 'flex', flexDirection: 'column', rowGap: '2.5rem' }}>
        <button onClick={goToEditAdmin} style={{ backgroundColor: 'var(--first-color)', color: 'var(--title-color)', border: 'none', borderRadius: '4px', cursor: 'pointer', transition: 'background-color 0.3s ease', padding: '0.75rem 1.5rem' }}>Edit Profile</button>
        <button onClick={goToApproveResidents} style={{ backgroundColor: 'var(--first-color)', color: 'var(--title-color)', border: 'none', borderRadius: '4px', cursor: 'pointer', transition: 'background-color 0.3s ease', padding: '0.75rem 1.5rem' }}>Approve Residents</button>
        <button onClick={() => setIsMenuOpen(false)} style={{ backgroundColor: 'var(--first-color)', color: 'var(--title-color)', border: 'none', borderRadius: '4px', cursor: 'pointer', transition: 'background-color 0.3s ease', padding: '0.75rem 1.5rem' }}>Logout</button>
      </div>
    </div>
  );
}

export default Home;
