import { Link } from 'react-router-dom';
import React, { useState } from 'react';
import axios from 'axios';

function Home() {
  const [notificationMessage, setNotificationMessage] = useState('');
  const [adminData, setAdminData] = useState({
    name: 'xyz',
    picture: '',
    contact: 'Admin Contact',
    address: 'Admin Address',
  });
  const [editMode, setEditMode] = useState(false); // State to toggle edit mode
  const [editedAdminData, setEditedAdminData] = useState({}); // State to hold edited admin data
  const [menuOpen, setMenuOpen] = useState(false); // State to toggle menu

  // Function to send notification
  const sendNotification = async () => {
    try {
      await axios.post('http://localhost:8000/api/notifications', { message: notificationMessage });
      console.log('Notification sent');
      setNotificationMessage('');
    } catch (error) {
      console.error('Error sending notification:', error);
    }
  };

  // Function to handle changes in admin data
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedAdminData({
      ...editedAdminData,
      [name]: value,
    });
  };

  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    setAdminData(editedAdminData);
    setEditMode(false);
  };

  // Function to handle image upload
  const handleImageUpload = (event) => {
    setEditedAdminData({
      ...editedAdminData,
      picture: URL.createObjectURL(event.target.files[0]),
    });
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'stretch', minHeight: '100vh', backgroundColor: '#f0f0f0', color: '#000' }}>
      {/* Menu Button */}
      <button onClick={() => setMenuOpen(!menuOpen)} style={{ position: 'fixed', top: '20px', left: '20px', background: 'none', border: 'none', cursor: 'pointer', zIndex: '999' }}>
        <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-menu">
          <line x1="3" y1="12" x2="21" y2="12"></line>
          <line x1="3" y1="6" x2="21" y2="6"></line>
          <line x1="3" y1="18" x2="21" y2="18"></line>
        </svg>
      </button>

      {/* Side Menu */}
      <div style={{ width: menuOpen ? '250px' : '0px', transition: 'width 0.3s', backgroundColor: '#2980b9', color: '#fff', height: '100vh', position: 'fixed', top: '0', left: '0', overflowX: 'hidden', paddingTop: '60px', zIndex: '998' }}>
        <Link to="/" style={{ display: 'block', padding: '20px', textDecoration: 'none', color: '#fff' }}>Home</Link>
        <Link to="/notifications" style={{ display: 'block', padding: '20px', textDecoration: 'none', color: '#fff' }}>Notifications</Link>
        <Link to="/settings" style={{ display: 'block', padding: '20px', textDecoration: 'none', color: '#fff' }}>Settings</Link>
        <button onClick={() => setEditMode(true)} style={{ display: 'block', padding: '10px 20px', marginTop: '20px', backgroundColor: '#fff', color: '#2980b9', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>Edit Profile</button>
      </div>

      {/* Main Content */}
      <div style={{ flex: '1', padding: '20px', marginLeft: menuOpen ? '250px' : '0px', transition: 'margin-left 0.3s', backgroundColor: '#f0f0f0', color: '#000' }}>
        <h1 style={{ marginBottom: '20px', color: '#2980b9' }}>Society Connect <br></br> Admin Dashboard</h1>

        {/* Admin Profile */}
        <div style={{ backgroundColor: '#fff', color: '#000', padding: '20px', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', marginBottom: '20px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <h2 style={{ color: '#2980b9', marginBottom: '20px' }}>Admin Profile</h2>
          <img src={adminData.picture} alt="Admin" style={{ width: '150px', height: '150px', borderRadius: '50%', marginBottom: '10px' }} />
          <div style={{ marginBottom: '10px' }}><strong>Name:</strong> {adminData.name}</div>
          <div style={{ marginBottom: '10px' }}><strong>Contact:</strong> {adminData.contact}</div>
          <div style={{ marginBottom: '10px' }}><strong>Address:</strong> {adminData.address}</div>
        </div>

        {/* Edit Profile Form */}
        {editMode && (
          <div style={{ backgroundColor: '#fff', color: '#000', padding: '20px', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', marginBottom: '20px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <h2 style={{ color: '#2980b9', marginBottom: '20px' }}>Edit Profile</h2>
            <form onSubmit={handleSubmit} style={{ width: '100%' }}>
              <label style={{ marginBottom: '10px' }}>Name:</label>
              <input type="text" name="name" value={editedAdminData.name || adminData.name} onChange={handleInputChange} style={{ marginBottom: '10px', width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
              <label style={{ marginBottom: '10px' }}>Contact:</label>
              <input type="text" name="contact" value={editedAdminData.contact || adminData.contact} onChange={handleInputChange} style={{ marginBottom: '10px', width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
              <label style={{ marginBottom: '10px' }}>Address:</label>
              <input type="text" name="address" value={editedAdminData.address || adminData.address} onChange={handleInputChange} style={{ marginBottom: '10px', width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
              <label style={{ marginBottom: '10px' }}>Profile Picture:</label>
              <input type="file" accept="image/*" onChange={handleImageUpload} style={{ marginBottom: '10px' }} />
              <button type="submit" style={{ padding: '8px 16px', cursor: 'pointer', backgroundColor: '#2980b9', color: '#fff', border: 'none', borderRadius: '5px' }}>Save</button>
            </form>
          </div>
        )}

        {/* Generate Notification */}
        <div style={{ backgroundColor: '#fff', color: '#000', padding: '20px', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', marginBottom: '20px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <h2 style={{ color: '#2980b9', marginBottom: '20px' }}>Generate Notification</h2>
          <textarea
            value={notificationMessage}
            onChange={(e) => setNotificationMessage(e.target.value)}
            placeholder="Enter notification message..."
            style={{ width: '100%', minHeight: '100px', marginBottom: '10px', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }}
          />
          <button onClick={sendNotification} style={{ padding: '8px 16px', cursor: 'pointer', backgroundColor: '#2980b9', color: '#fff', border: 'none', borderRadius: '5px' }}>Send Notification</button>
        </div>

        <br />
        <Link to="/" style={{ color: '#2980b9' }}>Logout</Link>
      </div>
    </div>
  );
}

export default Home;
