import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ServiceProvidersPage() {
  const [serviceProviders, setServiceProviders] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchServiceProviders = async () => {
      try {
        const response = await axios.get('http://192.168.164.157:8000/ServiceProvider');
        setServiceProviders(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching service providers:', error);
        setIsLoading(false);
      }
    };

    fetchServiceProviders();
  }, []);

  const handleDelete = async (email) => {
    try {
      await axios.delete(`http://192.168.164.157:8000/ServiceProvider/${email}`);
      setServiceProviders(serviceProviders.filter(serviceProvider => serviceProvider.email !== email));
    } catch (error) {
      console.error('Error deleting service provider:', error);
    }
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', minHeight: '100vh', backgroundColor: '#f0f0f0', color: '#000' }}>
      <h1 style={{ color: '#2980b9', marginBottom: '20px' }}>List of Service Providers</h1>
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0, width: '80%' }}>
          {serviceProviders.map((serviceProvider, index) => (
            <li key={index} style={{ marginBottom: '10px', borderBottom: '1px solid #ccc', paddingBottom: '10px', display: 'flex', justifyContent: 'space-between' }}>
              <div>
                <p>Email: {serviceProvider.email}</p>
                <p>Profession: {serviceProvider.profession}</p>
                <p>Contact: {serviceProvider.contact}</p>
                <p>Password: {serviceProvider.password}</p>
              </div>
              <button onClick={() => handleDelete(serviceProvider.email)}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default ServiceProvidersPage;
