import { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import Home from './Home';

function ApproveResidents() {
  // Sample data for residents waiting for approval
  const [residents, setResidents] = useState([
    { id: 1, name: 'John Doe', email: 'john@example.com' },
    { id: 2, name: 'Jane Smith', email: 'jane@example.com' },
    { id: 3, name: 'Alice Johnson', email: 'alice@example.com' },
  ]);

  // Function to approve a resident
  const approveResident = (id) => {
    // Perform approval logic (e.g., update database)
    console.log(`Resident with ID ${id} approved.`);
    // Remove the approved resident from the list
    setResidents(residents.filter(resident => resident.id !== id));
  };

  // Function to reject a resident
  const rejectResident = (id) => {
    // Perform rejection logic (e.g., notify user)
    console.log(`Resident with ID ${id} rejected.`);
    // Remove the rejected resident from the list
    setResidents(residents.filter(resident => resident.id !== id));
  };

  // Get the location object to determine the previous location
  const location = useLocation();

  // Function to go back to the previous location
  const goBack = () => {
    if (location.state && location.state.from) {
      return location.state.from;
    } else {
      return '/';
    }
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'stretch', minHeight: '100vh', backgroundColor: '#f0f0f0', color: '#000' }}>
      {/* Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '20px', borderBottom: '1px solid #ccc' }}>
        <h2>Approve Residents</h2>
        <Link to={Home()} style={{ padding: '10px 20px', textDecoration: 'none', color: '#2980b9', borderRadius: '5px', border: '1px solid #2980b9' }}>Back to Dashboard</Link>
      </div>

      {/* Body */}
      <div style={{ flex: 1, padding: '20px' }}>
        <h3 style={{ marginBottom: '20px' }}>Residents Waiting for Approval</h3>
        <div style={{ backgroundColor: '#fff', color: '#000', padding: '20px', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' }}>
          {residents.map(resident => (
            <div key={resident.id} style={{ marginBottom: '20px' }}>
              <h4>{resident.name}</h4>
              <p>Email: {resident.email}</p>
              <button onClick={() => approveResident(resident.id)} style={{ marginRight: '10px', padding: '8px 16px', cursor: 'pointer', backgroundColor: '#28a745', color: '#fff', border: 'none', borderRadius: '5px' }}>Approve</button>
              <button onClick={() => rejectResident(resident.id)} style={{ padding: '8px 16px', cursor: 'pointer', backgroundColor: '#dc3545', color: '#fff', border: 'none', borderRadius: '5px' }}>Don't Approve</button>
            </div>
          ))}
          {residents.length === 0 && <p>No residents waiting for approval.</p>}
        </div>
      </div>

      {/* Footer */}
      <div style={{ display: 'flex', justifyContent: 'center', padding: '20px', borderTop: '1px solid #ccc' }}>
        <Link to={goBack()} style={{ padding: '10px 20px', textDecoration: 'none', color: '#2980b9', borderRadius: '5px', border: '1px solid #2980b9' }}>Logout</Link>
      </div>
    </div>
  );
}

export default ApproveResidents;
