import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function EditProfile() {
  const [name, setName] = useState('Roohullah');
  const [profilePicture, setProfilePicture] = useState('/path/to/profile-photo.jpg');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleProfilePictureChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setProfilePicture(reader.result);
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const saveChanges = () => {
    // Simulate API call to save changes
    setTimeout(() => {
      setSuccessMessage('Profile successfully updated!');
      setErrorMessage('');
      // Pass updated data to Home component
      window.localStorage.setItem('userData', JSON.stringify({ name, profilePicture }));
    }, 1000);
  };

  const goBack = () => {
    // Go back to the previous page
    navigate(-1);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', minHeight: '100vh', backgroundColor: '#f0f0f0', color: '#000', padding: '20px' }}>
      <div style={{ position: 'absolute', top: '10px', left: '10px' }}>
        <button onClick={goBack}>Back</button>
      </div>
      <h2>Edit Profile</h2>
      <div style={{ marginBottom: '20px' }}>
        <label htmlFor="name">Name:</label>
        <input type="text" id="name" value={name} onChange={handleNameChange} />
      </div>
      <div style={{ marginBottom: '20px' }}>
        <label htmlFor="profile-picture">Profile Picture:</label>
        <input type="file" id="profile-picture" onChange={handleProfilePictureChange} />
      </div>
      <div style={{ marginBottom: '20px' }}>
        <img src={profilePicture} alt="Profile" style={{ width: '100px', height: '100px', borderRadius: '50%' }} />
      </div>
      <button onClick={saveChanges}>Save Changes</button>
      {successMessage && <p style={{ color: 'green', marginTop: '10px' }}>{successMessage}</p>}
      {errorMessage && <p style={{ color: 'red', marginTop: '10px' }}>{errorMessage}</p>}
    </div>
  );
}

export default EditProfile;
