import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ResidentsPage() {
  const [residents, setResidents] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchResidents = async () => {
      try {
        const response = await axios.get('http://192.168.164.157:8000/users');
        setResidents(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching residents:', error);
        setIsLoading(false);
      }
    };

    fetchResidents();
  }, []);

  const handleDelete = async (email) => {
    try {
      await axios.delete(`http://192.168.164.157:8000/users/${email}`);
      setResidents(residents.filter(resident => resident.email !== email));
    } catch (error) {
      console.error('Error deleting resident:', error);
    }
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', minHeight: '100vh', backgroundColor: '#f0f0f0', color: '#000' }}>
      <h1 style={{ color: '#2980b9', marginBottom: '20px' }}>List of Residents</h1>
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0, width: '80%' }}>
          {residents.map((resident, index) => (
            <li key={index} style={{ marginBottom: '10px', borderBottom: '1px solid #ccc', paddingBottom: '10px', display: 'flex', justifyContent: 'space-between' }}>
              <div>
                <p>Email: {resident.email}</p>
                <p>Password: {resident.password}</p>
              </div>
              <button onClick={() => handleDelete(resident.email)}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default ResidentsPage;
